package com.StarPattern;

public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
/*		*****
		*****
		*****
		*****     print this pattern  */
		
		
		int n = 4; // indicates rows 
		int m = 5; // columns 
		
		for(int i=1; i<=n ; i++) {
			 for (int j = 1; j<=m ; j++) {
				 System.out.print("*");
			 }
			 System.out.println();
		}
			

	}

}
